$(function(){$("#sky-form").validate({rules:{name:{required:!0},email:{required:!0,email:!0},message:{required:!0,minlength:10},captcha:{required:!0,remote:"sky-form/captcha/process.php"}},messages:{name:{required:"Please enter your name"},email:{required:"Please enter your email address",email:"Please enter a VALID email address"},message:{required:"Please enter your message"},captcha:{required:"Please enter characters",remote:"Correct captcha is required"}},submitHandler:function(e){$(e).ajaxSubmit({beforeSend:function(){$('#sky-form button[type="submit"]').attr("disabled",!0)},success:function(){$("#sky-form").addClass("submited")}})},errorPlacement:function(e,r){e.insertAfter(r.parent())}})});