function stream_layers(r,o,n){function t(r){for(var a=1/(.1+Math.random()),n=2*Math.random()-.5,t=10/(.1+Math.random()),e=0;e<o;e++){var m=(e/o-n)*t;r[e]+=a*Math.exp(-m*m)}}return arguments.length<3&&(n=0),d3.range(r).map(function(){var r,a=[];for(r=0;r<o;r++)a[r]=n+n*Math.random();for(r=0;r<5;r++)t(a);return a.map(stream_index)})}function stream_waves(r,t){return d3.range(r).map(function(n){return d3.range(t).map(function(r){var a=20*r/t-n/3;return 2*a*Math.exp(-.5*a)}).map(stream_index)})}function stream_index(r,a){return{x:a,y:Math.max(0,r)}}