function stream_layers(r,a,n){function t(r){for(var n=1/(.1+Math.random()),t=2*Math.random()-.5,e=10/(.1+Math.random()),m=0;a>m;m++){var o=(m/a-t)*e;r[m]+=n*Math.exp(-o*o)}}return arguments.length<3&&(n=0),d3.range(r).map(function(){var r,e=[];for(r=0;a>r;r++)e[r]=n+n*Math.random();for(r=0;5>r;r++)t(e);return e.map(stream_index)})}function stream_waves(r,a){return d3.range(r).map(function(r){return d3.range(a).map(function(n){var t=20*n/a-r/3;return 2*t*Math.exp(-.5*t)}).map(stream_index)})}function stream_index(r,a){return{x:a,y:Math.max(0,r)}}